mapper.select:
SELECT 

FROM sm_symptom a

WHERE a.id = #{id}

SELECT 

FROM sm_symptom a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.qy_status = #{qyStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.sort ASC, a.create_date DESC

SELECT 

FROM sm_symptom a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sm_symptom SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
